import { MachineConfig, send, Action, assign } from "xstate";


function say(text: string): Action<SDSContext, SDSEvent> {
    return send((_context: SDSContext) => ({ type: "SPEAK", value: text }))
}

function listen(): Action<SDSContext, SDSEvent> {
    return send('LISTEN')
}

const grammar: { [index: string]: { person?: string, day?: string, time?: string } } = {
    "John": { person: "John Appleseed" }, 
    "Joe": { person: "Joe Biden" },
    "Barack": { person: "Barack Obama" },
    "on Friday": { day: "Friday" },
    "at ten": { time: "10:00" },
}

const bybio_vagina: { [index: string]: { yes?: string, no?: string, maybe?: string } } = { /* pakeisk pavadinimus, pyzda */
    "do not know": { maybe: "probably" },
    "yes": { yes: "yes indeed" },
    "no": { no: "no for sure" },

}

export const dmMachine: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'init',
    states: {
        init: {
            on: {
                CLICK: 'welcome'
            }
        },
        welcome: {
            initial: "prompt",
            on: { ENDSPEECH: "who" },
            states: {
                prompt: { entry: say("Let's create an appointment") }
            }
        },
        who: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "person" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { person: grammar[context.recResult].person } }),
                    target: "day"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("Who are you meeting with?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't know them"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        day: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "day" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { day: grammar[context.recResult].day } }),
                    target: "whole_day"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `OK. ${context.person}. On which day is your meeting?`   
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I did not understand"),
                    on: { ENDSPEECH: "prompt" }
                } 
            }
        },
        whole_day: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "yes" in (bybio_vagina[context.recResult] || {}),
                    actions: assign((context) => { return { yes: bybio_vagina[context.recResult].yes } }),
                    target: "app"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("Will it take the whole day?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Please repeat"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        not_whole_day: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "no" in (bybio_vagina[context.recResult] || {}),
                    actions: assign((context) => { return { no: bybio_vagina[context.recResult].no } }),
                    target: "time"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("Will it take the whole day?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Please repeat"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        app: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "yes" in (bybio_vagina[context.recResult] || {}),
                    actions: assign((context) => { return { yes: bybio_vagina[context.recResult].yes } }),
                    target: "done"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want me to create an appointment with ${context.person} on ${context.day} for the whole day?`   
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Please repeat"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        app_no: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "no" in (bybio_vagina[context.recResult] || {}),
                    actions: assign((context) => { return { no: bybio_vagina[context.recResult].no } }),
                    target: "who"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want me to create an appointment with ${context.person} on ${context.day} for the whole day?`   
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Please repeat"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        done: {
            initial: "prompt",
            on: { ENDSPEECH: "init" },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Your appointment has been created!`
                    }))
                }
            }
        },
        time: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "time" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { time: grammar[context.recResult].time } }),
                    target: "app2"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `${context.day} it is. What time is your meeting?`   
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry could you repeat"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        app2: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "yes" in (bybio_vagina[context.recResult] || {}),
                    actions: assign((context) => { return { yes: bybio_vagina[context.recResult].yes } }),
                    target: "done"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want me to create an appointment with ${context.person} on ${context.day} at ${context.time}?`   
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Please repeat"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        app2_no: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "no" in (bybio_vagina[context.recResult] || {}),
                    actions: assign((context) => { return { no: bybio_vagina[context.recResult].no } }),
                    target: "who"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want me to create an appointment with ${context.person} on ${context.day} for the whole day?`   
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Please repeat"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        }
    }
})
